import base64

instructions = eval(base64.b64decode('').decode())

posx = 0
posy = 0
dirx = 0
diry = 1

right_switch = 0
count_left = 0

for line in instructions:
    instruction = line.strip()
    if line == '':
        break

    if instruction == "vor":
        posx += dirx
        posy += diry
        count_left = 0

    elif instruction == "links":
        dirx, diry = -diry, dirx
        count_left += 1

    if count_left == 3:
        right_switch += 1
        count_left = 0

print(posx, posy)
print(right_switch)
